import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';

function ScheduleWidget() {
  // Converts time strings to Date objects
  const parseTime = (timeStr) => {
    const [hours, minutes] = timeStr.split(':');
    return new Date(0, 0, 0, parseInt(hours), parseInt(minutes));
  };

  // Calculate the total number of hours between two time strings
  const calculateTotalHours = (from, to) => {
    const fromTime = parseTime(from);
    const toTime = parseTime(to);
    const hourDiff = (toTime - fromTime) / (1000 * 60 * 60);
    return parseFloat(Math.abs(hourDiff).toFixed(2));
  };

  const [schedule, setSchedule] = React.useState(appsmith.model);

  React.useEffect(() => {
    appsmith.onModelChange((model, prevModel) => {
      if (JSON.stringify(model.data) !== JSON.stringify(prevModel?.data)) {
        setSchedule(model.data);
      }
    });
  }, []);

  const handleDelete = (day, index) => {
    const confirmDelete = confirm(`Sicuri di cancellare questo turno ${day}?`);
    if (confirmDelete) {
      console.log(`Delete ${day} slot ${index}`);
      const updatedSchedule = { ...schedule };
      updatedSchedule[day].splice(index, 1);
      setSchedule(updatedSchedule);
      appsmith.triggerEvent("onAction", { day, action: "delete", index });
    }
  };

  return (
    <div className="schedule">
      {Object.entries(schedule).map(([day, slots]) => (
        <div className="day-container" key={day}>
          <div className="day-header">{day}</div>
          {slots.map((slot, index) => (
            <div className="time-slot" key={index}>
              <button className="delete-button" onClick={() => handleDelete(day, index)}>
                <i className="fas fa-trash"></i>
              </button>
              <div className="time-details">
                <div>
                  <span>{slot.from}</span> - <span>{slot.to}</span>
                  <div className="total-hours">
                    {calculateTotalHours(slot.from, slot.to)} ore
                  </div>
                </div>
              </div>
              <div className="location-and-buttons">
                <span>{slot.location}</span>
              </div>
              <div className="district">
                <span>{slot.district}</span>
              </div>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

appsmith.onReady(() => {
  ReactDOM.render(<ScheduleWidget />, document.getElementById("root"));
});
